DROP TABLE IF EXISTS `tbl_deviceinfo_mst`;
CREATE TABLE `tbl_deviceinfo_mst` (
  `DeviceID` varchar(15) NOT NULL COMMENT '设备ID\\n\\''\\''W\\''+6位测点ID+8位设备序列号',
  `DeviceName` varchar(20) default NULL COMMENT '设备名称',
  `RegionID` varchar(6) NOT NULL COMMENT '测点ID\\n设备测点（区域），关联RegionInfo表',
  `ValidFlag` varchar(1) NOT NULL default '1' COMMENT '设备有效区分\\n判别设备是否有效，如无效，则不采集数据，也不上传。\\n1：有效\\n0：无效\\n※需要和设备方的接口确认',
